script.source:
#!/bin/bash
# Fixup etckeeper if [ -f /etc/etckeeper/etckeeper.conf ] then cp /etc/etckeeper/etckeeper.conf /etc/etckeeper/etckeeper.conf.orig ( cat /etc/etckeeper/etckeeper.conf | \ sed -e 's/^#VCS=\"hg\"/VCS=\"hg\"/' \ -e 's/^VCS=git/#VCS=\"git\"/' ) > /etc/etckeeper/etckeeper.conf.new mv /etc/etckeeper/etckeeper.conf.new /etc/etckeeper/etckeeper.conf etckeeper init etckeeper commit "Initial commit of base system" fi
# Force root password change chage -d 0 root etckeeper commit "Force root to change password"
